mapper.select:

SELECT * FROM
 (SELECT B.*, ROW_NUMBER() OVER(ORDER BY BOARD_NO DESC) RNUM FROM BOARD B)
 	WHERE RNUM >= #{startNo} AND RNUM <= #{endNo}

mapper.update:
UPDATE BOARD SET
	 BOARD_TITLE = #{board_title}, BOARD_CONTENT = #{board_cont}, BOARD_UPDATE = SYSDATE
	 WHERE BOARD_NO = #{board_no}
